INSERT INTO `blogs` (`id`, `user_id`, `category_id`, `subcategory_id`, `title`, `summary`, `post`, `slug`, `thumbnail`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 1, 1, 1, 'Effects Names PP', 'Visual effects created in the computer that provide interesting transitions from one scene to another rather than just switching frames. They include fading or dissolving the frame, wiping one frame over another and flipping the frame. See wipe, morphing and digital effects.', '<table align=\"left\" border=\"1\" cellpadding=\"5\" cellspacing=\"1\" style=\"width:100%\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Effects Name</th>\r\n			<th scope=\"col\">Work</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>Horizontal Flip</td>\r\n			<td>For Flip Video</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Linear Wipe</td>\r\n			<td>Wipe Video</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Warp Stabilizer</td>\r\n			<td>Hand record video fix</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Gaussian Blur, Crop</td>\r\n			<td>Identify Hide</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ultra Key</td>\r\n			<td>Green Screen Remove</td>\r\n		</tr>\r\n		<tr>\r\n			<td>reverb</td>\r\n			<td>Audio Nose Cancel</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Delay</td>\r\n			<td>Room Effects</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Denoiser</td>\r\n			<td>Noise Remove</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Three-way color corrector</td>\r\n			<td>Color Correction</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Track matte key</td>\r\n			<td>Clip Shape</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Camera Blur</td>\r\n			<td>Auto 25% Blur</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', 'effects-names-pp', 'effects-names-pp.jpg', '2021-05-08 22:48:47', '2021-06-06 02:15:06', NULL),
(2, 1, 1, 1, 'Custom Sequence Preset Configuration', ' Pixel Aspect Ratio: D1/DV NTSC Widescreen 16:9 (1.2121)\r\n Timebase: 29.97 fps', '<p>General<br />\r\n&nbsp;Editing mode: Custom<br />\r\n&nbsp;Timebase: 29.97 fps</p>\r\n\r\n<p>Video Settings<br />\r\n&nbsp;Frame size: 1920h 1080v (1.2121)<br />\r\n&nbsp;Frame rate: 29.97 &nbsp;frames/second<br />\r\n&nbsp;Pixel Aspect Ratio: D1/DV NTSC Widescreen 16:9 (1.2121)<br />\r\n&nbsp;Fields: Lower Field First</p>\r\n\r\n<p>Audio Settings<br />\r\n&nbsp;Sample rate: 48000 samples/second</p>\r\n\r\n<p>Default Sequence<br />\r\n&nbsp;Total video tracks: 3<br />\r\n&nbsp;Master track type: Stereo<br />\r\n&nbsp;Audio Tracks:<br />\r\n&nbsp;Audio 1: Standard<br />\r\n&nbsp;Audio 2: Standard<br />\r\n&nbsp;Audio 3: Standard</p>', 'custom-sequence-preset-configuration', 'custom-sequence-preset-configuration.jpg', '2021-05-30 23:49:06', '2021-05-30 23:49:06', NULL),
(3, 1, 2, 3, 'Business Card', 'Standard business cards are 3.5\"x 2\" – but have you ever thought about why most cards are exactly that size and shape? You may have noticed that they generally all fit nicely into a wallet, purse or pocket. Gift cards, credit cards and licenses are roughly the same size for just that reason.', '<p><strong>01. New Document</strong></p>\r\n\r\n<p>Horizontal Business Card Size: 3.5x2 Inch</p>\r\n\r\n<p>Verticle Business Card Size: 2x3.5 Inch</p>\r\n\r\n<p>Color Mode: CMYK</p>\r\n\r\n<p>Resolution: 300</p>\r\n\r\n<p>New Document Size: 3.25&quot;x1.75&quot;</p>\r\n\r\n<hr />\r\n<p><strong>02. New Shape Document Size</strong></p>\r\n\r\n<p>Then Background delete.</p>\r\n\r\n<hr />\r\n<p><strong>03. New Bleed 01</strong></p>\r\n\r\n<p>Go to Image&gt;Canvas Size (Alt+Ctrl+C)</p>\r\n\r\n<p>Canvas Size: (0.25x0.25)</p>\r\n\r\n<p>&amp; Take new substruct shape</p>\r\n\r\n<hr />\r\n<p><strong>04. New Bleed 02 Rounded</strong></p>\r\n\r\n<p>Go to Image&gt;Canvas Size (Alt+Ctrl+_C)</p>\r\n\r\n<p>Canvas Size: (0.25x0.25)</p>\r\n\r\n<p>&amp; Take new substruct shape</p>\r\n\r\n<hr />\r\n<p>Title Size: 9-14 pt</p>\r\n\r\n<p>Designation Size: 6-8 pt</p>\r\n\r\n<p>Letter Space: 220 pt</p>\r\n\r\n<p>&nbsp;</p>', 'business-card', 'business-card.jpg', '2021-06-06 12:20:33', '2021-06-06 18:08:12', '2021-06-06 18:08:12'),
(4, 1, 2, 4, 'Business Card Make', 'Standard business cards are 3.5\"x 2\" – but have you ever thought about why most cards are exactly that size and shape? You may have noticed that they generally all fit nicely into a wallet, purse or pocket. Gift cards, credit cards and licenses are roughly the same size for just that reason.', '<p><strong>01. New Document</strong></p>\r\n\r\n<p>Horizontal Business Card Size: 3.5x2 Inch</p>\r\n\r\n<p>Verticle Business Card Size: 2x3.5 Inch</p>\r\n\r\n<p>Color Mode: CMYK</p>\r\n\r\n<p>Resolution: 300</p>\r\n\r\n<p>New Document Size: 3.25&quot;x1.75&quot;</p>\r\n\r\n<hr />\r\n<p><strong>02. New Shape Document Size</strong></p>\r\n\r\n<p>Then Background delete.</p>\r\n\r\n<hr />\r\n<p><strong>03. New Bleed 01</strong></p>\r\n\r\n<p>Go to Image&gt;Canvas Size (Alt+Ctrl+C)</p>\r\n\r\n<p>Canvas Size: (0.25x0.25)</p>\r\n\r\n<p>&amp; Take new substruct shape</p>\r\n\r\n<hr />\r\n<p><strong>04. New Bleed 02 Rounded</strong></p>\r\n\r\n<p>Go to Image&gt;Canvas Size (Alt+Ctrl+_C)</p>\r\n\r\n<p>Canvas Size: (0.25x0.25)</p>\r\n\r\n<p>&amp; Take new substruct shape</p>\r\n\r\n<hr />\r\n<p>Title Size: 9-14 pt</p>\r\n\r\n<p>Designation Size: 6-8 pt</p>\r\n\r\n<p>Letter Space: 220 pt</p>', 'business-card-make', 'business-card-make.jpg', '2021-06-06 18:09:31', '2021-06-06 18:09:31', NULL),
(5, 1, 2, 4, 'Flayer Design', 'A flyer (or flier) is a form of paper advertisement intended for wide distribution and typically posted or distributed in a public place, handed out to individuals or sent through the mail. In the 2010s, flyers range from inexpensively photocopied leaflets to expensive, glossy, full-color circulars.', '<p><strong>01. New Documents</strong></p>\r\n\r\n<ul>\r\n	<li>Preset: U.S Paper</li>\r\n	<li>Original Size: 8.5&quot;x11&quot;</li>\r\n	<li>Size: 8.27&quot;x11.69&quot;</li>\r\n	<li>Take Size: 8&quot;x10.5&quot;</li>\r\n	<li>Resolution: 300</li>\r\n	<li>Color: CMYK</li>\r\n</ul>\r\n\r\n<hr />\r\n<p><strong>02. Canvas Size</strong></p>\r\n\r\n<p>Go to Image&gt;Canvas Size</p>\r\n\r\n<ul>\r\n	<li>width: 0.5&quot;</li>\r\n	<li>Height: 0.5&quot;</li>\r\n</ul>\r\n\r\n<hr />\r\n<p><strong>03. Canvas Size</strong></p>\r\n\r\n<p>Go to Image&gt;Canvas Size</p>\r\n\r\n<ul>\r\n	<li>width: 0.25&quot;</li>\r\n	<li>Height: 0.25&quot;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>', 'flayer-design', 'flayer-design.jpg', '2021-06-10 11:55:47', '2021-06-10 18:03:11', NULL),
(6, 1, 1, 5, 'Adobe After Effects (Effects Name)', 'Adobe After Effects is a digital visual effects, motion graphics, and compositing application developed by Adobe Systems and used in the post-production process of film making, video games and television production. Among other things, After Effects can be used for keying, tracking, compositing, and animation.', '<p><strong>Effects Name</strong></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"5\" cellspacing=\"1\" style=\"width:100%\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"col\">Effects Name</th>\r\n			<th scope=\"col\">Effects Work</th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<td>CC Mr. Marqury</td>\r\n			<td>Smook Filter</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Audio Spectrum</td>\r\n			<td>&nbsp;Audio Visualization</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Polar Coordinates</td>\r\n			<td>Audio Visualization Circle</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tint</td>\r\n			<td>Image black &amp; white</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Turbulent Displace</td>\r\n			<td>Curve Shape</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ramp</td>\r\n			<td>Shape color change</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Hue/Saturation</td>\r\n			<td>Gradian color</td>\r\n		</tr>\r\n		<tr>\r\n			<td>KeyLight (1.2)</td>\r\n			<td>Green Screen Remove</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', 'adobe-after-effects-effects-name', 'adobe-after-effects-effects-name.jpg', '2021-06-14 12:15:41', '2021-06-30 14:31:22', NULL),
