INSERT INTO `professions` (`id`, `user_id`, `resume_id`, `title`, `name`, `start`, `end`, `description`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 1, 1, 'Job', 'Apex Footwear Limited, Unit-02', '12/06/2016', NULL, '<p>Born In Bangladesh, Worn Around The World. From a humble beginning to the&nbsp;<em>apex</em>&nbsp;of innovation,&nbsp;<em>Apex</em>&nbsp;has perfected the art of Orginal Design Manufacturing.</p>\r\n\r\n<table align=\"left\" border=\"1\" cellpadding=\"5\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<th scope=\"row\">ID</th>\r\n			<td>U2F006159</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Department</th>\r\n			<td>Synthetic</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Section</th>\r\n			<td>Flip Flop</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Designation</th>\r\n			<td>Supervisor</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Joining Date</th>\r\n			<td>12 July 2016</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', '2021-05-07 23:41:21', '2021-05-07 23:41:21', NULL),
(2, 1, 1, 'Microsoft Office & English Spoken', 'Orbit Computer Training Center', '01/10/2018', '01/10/2019', '<table align=\"left\" border=\"1\" cellpadding=\"5\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<th scope=\"row\">Department</th>\r\n			<td>Microsoft Office &amp; English Spoken</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Trade Name &amp; Code</th>\r\n			<td>Computer Office Applications - (76)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Registration</th>\r\n			<td>3000552079</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Session</th>\r\n			<td>October - December (2019)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Board</th>\r\n			<td>Bangladesh Technical Education Board Dhaka</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', '2021-05-08 00:53:29', '2021-05-08 01:16:33', NULL),
(3, 1, 1, 'Professional Web Development', 'Creative IT Institute', '19/06/2020', '06/02/2021', '<p>Creative IT Institute, one of the leading IT training institutes in Bangladesh offers the best training opportunities. It has been playing a vital role to eradicate the unemployment problem since 2008. Enriched with quality training this institute has never failed to help individuals to reveal their talents making harmony between creativity and IT. No matter what is your background, we are offering 30 courses for you conducted by experienced trainers to advance your skills.</p>\r\n\r\n<p><a href=\"https://www.google.com/search?rlz=1C1CHBF_enBD933BD933&amp;sxsrf=ALeKk00YTpcVmitWeS1LhJiwQQcbkgxCsw:1620434872949&amp;q=creative+it+institute+address&amp;stick=H4sIAAAAAAAAAOPgE-LVT9c3NEwyLc_KrbLM1pLNTrbSz8lPTizJzM-DM6wSU1KKUouLF7HKJhelAoXKUhUySxQy84pLMktKS1IVoPIADkU0_lAAAAA&amp;ludocid=18214157927961281514&amp;sa=X&amp;ved=2ahUKEwjG8u6n7rjwAhWm7XMBHTQIAGMQ6BMwGHoECCUQAg\">Address</a>:&nbsp;Momtaz Plaza (4th floor), House#7, Road#4 Opposite of Labaid Hospital, Dhaka 1205</p>\r\n\r\n<table align=\"left\" border=\"1\" cellpadding=\"5\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<th scope=\"row\">Department</th>\r\n			<td>Professional Web Development</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Batch</th>\r\n			<td>ES WEB DEV 2001</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">ID</th>\r\n			<td>20040104</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Web Language</th>\r\n			<td>PHP</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Framework</th>\r\n			<td>Laravel (PHP)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Trainer</th>\r\n			<td><a href=\"https://www.facebook.com/Hasan157\">Tarique Hasan</a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', '2021-05-08 00:53:29', '2021-05-08 00:55:22', NULL),
(4, 1, 1, 'Professional Video Editing', 'UY Lab', '12/04/2021', '23/06/2021', '<p>I edit videos just to do my own professional work,&nbsp;Videos are the most demanding content of the era. 300 hours of video are uploaded to YouTube every minute! In this course, I&rsquo;ll be able to learn how to start &amp; finish a complete editing project from the scratch. Also, I&nbsp;want to be a freelancer or youtube content creator, we&rsquo;re here to help! From opening a brand new youtube channel to working in a giant marketplace like Fiverr &amp; freelancer, everything is served hot in this course.</p>\r\n\r\n<table align=\"left\" border=\"1\" cellpadding=\"5\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<th scope=\"row\">Institute</th>\r\n			<td>UY Lab</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Course</th>\r\n			<td>Professional Video Editing</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Batch</th>\r\n			<td>UY LAB ONLINE (VE-2107)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Trainer</th>\r\n			<td><a href=\"https://www.facebook.com/groups/201271254723557/user/100028149024172\">Upoma Anindita</a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', '2021-05-09 20:55:02', '2021-07-24 19:04:06', NULL),
(5, 1, 1, 'Professional Graphic Design', 'UY Lab', '27/04/2021', '16/07/2021', '<p>Graphic design is the art, profession and academic discipline whose activity consists in projecting visual communications intended to transmit specific messages to social groups, with specific objectives.</p>\r\n\r\n<table align=\"left\" border=\"1\" cellpadding=\"5\" cellspacing=\"1\" style=\"width:100%\">\r\n	<tbody>\r\n		<tr>\r\n			<th scope=\"row\">Institute</th>\r\n			<td>UY Lab</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Course</th>\r\n			<td>Professional Graphic Design</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Batch</th>\r\n			<td>UY LAB GD (ONLINE-2103)</td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\">Trainer</th>\r\n			<td><a href=\"https://www.facebook.com/groups/209633497306136/user/100007980962312/\">Abdullah Al Mahmud</a></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>', '2021-05-09 21:00:06', '2021-07-24 18:56:40', NULL);

